CREATE TABLE Cartera (id BIGINT AUTO_INCREMENT, idcustomer BIGINT, invoicenumber BIGINT, totalvalue FLOAT(10, 2), balance FLOAT(10, 2), taxvalue FLOAT(10, 2), createdate DATETIME, duedate DATETIME, created_at DATETIME, updated_at DATETIME, INDEX idcustomer_idx (idcustomer), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE Cliente (idcustomer BIGINT AUTO_INCREMENT, identification VARCHAR(255), businessname VARCHAR(255), tradename VARCHAR(255), credit FLOAT(10, 2), isblocked TINYINT(1) DEFAULT '0', state TINYINT(1) DEFAULT '1', avalilablecredit FLOAT(10, 2), idpricelist BIGINT, created_at DATETIME, updated_at DATETIME, PRIMARY KEY(idcustomer)) ENGINE = INNODB;
CREATE TABLE Producto (id BIGINT AUTO_INCREMENT, code VARCHAR(255), productref VARCHAR(255), name VARCHAR(255), unit VARCHAR(20), currencysymbol VARCHAR(5), description VARCHAR(255), idbrand BIGINT, brand VARCHAR(255), idcategory BIGINT, categoryname VARCHAR(255), idsubcategory BIGINT, subcategoryname VARCHAR(255), idline BIGINT, linename VARCHAR(255), suppliername VARCHAR(255), price FLOAT(10, 2), tax FLOAT(10, 2), discountprice FLOAT(10, 2), state TINYINT(1) DEFAULT '1', created_at DATETIME, updated_at DATETIME, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE Sede_Cliente (id BIGINT AUTO_INCREMENT, idcustomer BIGINT, identification VARCHAR(255), idcustomeroffice VARCHAR(255), address VARCHAR(255), name VARCHAR(200), countryid BIGINT, countryname VARCHAR(100), cityid BIGINT, cityname VARCHAR(50), contactperson1 VARCHAR(50), cellphonecontact VARCHAR(50), phonenumber VARCHAR(50), created_at DATETIME, updated_at DATETIME, INDEX idcustomer_idx (idcustomer), PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE Cartera ADD CONSTRAINT Cartera_idcustomer_Cliente_idcustomer FOREIGN KEY (idcustomer) REFERENCES Cliente(idcustomer);
ALTER TABLE Sede_Cliente ADD CONSTRAINT Sede_Cliente_idcustomer_Cliente_idcustomer FOREIGN KEY (idcustomer) REFERENCES Cliente(idcustomer);
